import React, { useState } from 'react';
import Facade from '../login/ApiFacade';
import Login from '../login/Login';
import LoggedIn from '../login/LoggedIn';

export default function Welcome() {

    const [state, setState] = useState();
    const [loggedIn, setLoggedIn] = useState(false);
    const [error, setError] = useState();


    const onSubmit = (evt) => {
        evt.preventDefault();
        login(state.username, state.password);
    }


    const login = (user, pass) => {
        Facade.login(user, pass)
            .then(res => { setLoggedIn(true); setError("") })
            .catch(e => setError("Wrong username or password"));

    }

    const onChange = (evt) => {
        setState({ ...state, [evt.target.id]: evt.target.value });
    }

    return (
        <div className="container-fluid">
            {!loggedIn ? (
            <div className="row">
                <div className="col welcome-col-1">
                    <img className="welcome-img-1" src="Rift-1.jpg"></img>
                </div>
                <div className="col welcome-col-2">
                    <div className="wrapper fadeInDown">
                        <div id="formContent">
                            <div className="fadeIn first">
                                <p className="login-form-header"> Login</p>
                            </div>
                            <form onSubmit={onSubmit} onChange={onChange}>
                                <input type="text" id="login" class="fadeIn second" name="login" placeholder="Username"></input>
                                <input type="password" id="password" class="fadeIn third" name="login" placeholder="Password"></input>
                                <input type="submit" class="fadeIn fourth" value="Log In"></input>
                            </form>
                        </div>
                    </div>
                </div>
            </div>) : (<div>
                    <LoggedIn />
                    <button className="btn btn-primary" onClick={logout}>Logout</button>
                </div>)}
        </div>
    )
}
